import 'package:flutter/material.dart';
import 'package:testing/constant/constant.dart';

class MyTheme {
  static ThemeData lightTheme(BuildContext context) => ThemeData(
        scaffoldBackgroundColor: kPrimaryLightColor,
        appBarTheme: const AppBarTheme(
          color: kPrimaryLightColor,
          iconTheme: IconThemeData(color: Colors.black),
        ),
        iconTheme: const IconThemeData(
          color: Colors.black,
        ),
        cardTheme: CardTheme(
            elevation: 3,
            margin: EdgeInsets.zero,
            shape: RoundedRectangleBorder(
                borderRadius: BorderRadius.circular(10))),
        elevatedButtonTheme: elevatedButtons(context),
      );

  static ThemeData darkTheme(BuildContext context) => ThemeData(
        primaryColor: kPrimaryDarkColor,
        brightness: Brightness.dark,
        scaffoldBackgroundColor: kPrimaryDarkColor,
        cardColor: kSecondaryDarkColor,
        appBarTheme: const AppBarTheme(
          color: kPrimaryDarkColor,
        ),
        iconTheme: const IconThemeData(
          color: Colors.white,
        ),
        elevatedButtonTheme: ElevatedButtonThemeData(
            style: ElevatedButton.styleFrom(
          shape: const StadiumBorder(),
          textStyle: Theme.of(context).textTheme.subtitle1,
          fixedSize: const Size(180, 45),
        )),
      );

  static ElevatedButtonThemeData elevatedButtons(BuildContext context) =>
      ElevatedButtonThemeData(
          style: ElevatedButton.styleFrom(
        shape: const StadiumBorder(),
        textStyle: Theme.of(context).textTheme.subtitle1,
        fixedSize: const Size(180, 45),
      ));
}
